int main(void)
{	
	platform_init();
	init_uart();
	trigger_setup();	
	simpleserial_init();

  simpleserial_addcmd('p',33,get_msg);
  //simpleserial_addcmd('k',1,get_nonce);

	while(1)
		simpleserial_get();
}